export const metricConfig = {
  ptf: { label: "PTF (TL/MWh)", unit: "TL/MWh", decimals: 0 },
  consumption: { label: "Tüketim (kWh)", unit: "kWh", decimals: 2 },
  cost: { label: "Enerji Maliyeti (TL)", unit: "TL", decimals: 2 },
};

export function getMetricRange(metric: HeatmapMetric): [number, number] {
  const allValues = invoiceDailyData.flatMap(d => d[metric]);
  return [Math.min(...allValues), Math.max(...allValues)];
}

export function getHeatmapData(metric: HeatmapMetric) {
  return invoiceDailyData.map(d => ({
    dateLabel: d.dateLabel,
    hours: d[metric],
  }));
}
